def id_highest_seat_id(seats)
  max = 0
  
  seats.each do |seat|
    row_high = 127
    row_low = 0
    col_high = 7
    col_low = 0
    row = seat[0..6]
    col = seat[7..9]

    row.chars.each do |c|
      row_mid = ((row_high - row_low) / 2) + row_low
      if (row_high - row_low) > 1
        if c == "F"
          row_high = row_mid
        else
          row_low = row_mid + 1
        end
      else
        if c == "F"
          row = row_low
        else
          row = row_high
        end
      end
    end

    col.chars.each do |c|
      col_mid = ((col_high - col_low) / 2) + col_low
      if (col_high - col_low) > 1
        if c == "L"
          col_high = col_mid
        else
          col_low = col_mid + 1
        end
      else
        if c == "L"
          col = col_low
        else
          col = col_high
        end
      end
    end

    id = (row * 8) + col
    max = (id > max) ? id : max
  end

  max
end

p id_highest_seat_id(puzzle_input)